import express from "express"
import cors from "cors"
import {
  modelRestEndpoints,
  createSimplyServer,
  verifyAuth,
  alwaysAllowed
} from "simply-served"
import todoDb from "./todoDb"
import {z} from "zod"
import {Todo, TodoDb} from "./types"

const app = express()

app.use(express.json())
app.use(cors()) // Disable cors

type ServerCtx = {
  db: TodoDb
  auth?: {userId: string}
}

const server = createSimplyServer<ServerCtx>({
  initContext: {
    db: todoDb,
    auth: {userId: ""}
  },
  middleware: verifyAuth("encryption-key"),
  controllers: {
    //  Autogenerated rest endpoints for the Todo model
    todo: modelRestEndpoints<ServerCtx, Todo>({
      collection: (db) => db.todo,
      validator: z.object({
        _id: z.string().uuid(),
        todoItem: z.string(),
        owner: z.string().uuid(),
        done: z.boolean().default(true)
      }),
      permissions: alwaysAllowed(),
      skipAuth: true
    }),
    // Autogenerated rest endpoints for the User model
    user: modelRestEndpoints({
      collection: (db) => db.user,
      validator: z.object({
        _id: z.string().uuid(),
        name: z.string()
      }),
      permissions: {
        create: () => ({Always: true}),
        read: () => ({Always: true}),
        modify: ({auth}) => ({_id: {Equal: auth?.userId ?? ""}}),
        // Never allow for users to be deleted
        delete: () => ({Never: true})
      },
      skipAuth: {
        // Allow unauthenticated users to view users
        get: true,
        query: true
      }
    })
  },
  afterGenerateEndpoints: (app) => {
    app.use("/", async (_req, res): Promise<any> => {
      return res.status(200).json("Welcome to my server!")
    })
  }
})

server.generateEndpoints(app)

console.info("Listening on port 8080\n")
console.info("Check out /frontend/todo.html to interact with the server")

app.listen(8080)
